def premier(journalists)
    puts "########################################################################"
    puts "Exercice n°1 : Combien y'a t-il de handles dans ce tableau ?"
    print "\n"
    puts "Nombre de handles = #{journalists.count}"
    print "\n"
    puts "Pour continuer appuyer sur ENTREE"
    puts "########################################################################"
    gets.chomp
end

def deuxieme(journalists)
    puts "########################################################################"
    puts "Exercice n°2 : Handle le plus court ?"
    print "\n"
    puts "Handle le plus court : " + journalists.min {|a, b| a.size <=> b.size}
    print "\n"
    puts "Pour continuer appuyer sur ENTREE"
    puts "########################################################################"
    gets.chomp
end

def troisieme(journalists)
    puts "########################################################################"
    puts "Exercice n°3 : Combien y'a t-il de handles avec 5 caractères dans ce tableau ?"
    five_char_username = journalists.count {|five_char| five_char.size == 6}
    puts five_char_username
    puts "Pour continuer appuyer sur ENTREE"
    puts "########################################################################"
    gets.chomp
end

def quatrieme(journalists)
    puts "########################################################################"
    puts "Exercice n°4 : Combien y'a t-il de handles qui commencent par une majuscule dans ce tableau ?"
    puts "Il y a #{journalists.count {|e| e =~ /^@[A-Z]/}} qui commencent par une majuscule"
    puts "Pour continuer appuyer sur ENTREE"
    puts "########################################################################"
    gets.chomp
end

def cinquieme(journalists)
    puts "########################################################################"
    puts "Exercice n°5 : Trier le tableau par ordre alphabétique des handles"
    liste = journalists.sort_by(&:upcase)
    puts liste
    puts "Pour continuer appuyer sur ENTREE"
    puts "########################################################################"
    gets.chomp
end

def sixieme(journalists)
    puts "########################################################################"
    puts "Exercice n°6 : Trier le tableau par ordre de taille des handles"
    liste = journalists.sort_by(&:length)
    puts liste
    puts "Pour continuer appuyer sur ENTREE"
    puts "########################################################################"
    gets.chomp
end

def septieme(liste)
    puts "########################################################################"
    puts "Exercice n°7 : À quelle place dans le tableau ce trouve le compte @epenser ?"
    puts liste.find_index("@epenser")
    puts "Pour continuer appuyer sur ENTREE"
    puts "########################################################################"
    gets.chomp
end

def huitieme(journalists)
    puts "########################################################################"
    puts "Exercice n°8 : Répartir par taille les handles avec 1, 2 caractères ou plus"
    puts "Les twitter handle triés par nombre de caractères :"
    print "\n"
    y = 2
    i = 33
    a = 1
    while y <= i
        if (journalists.count {|x| x.size == y}) !=0
        puts "Il y a #{journalists.count {|x| x.size == y}} handle(s) avec #{a} caractère(s)."
        end
        y += 1
        a += 1
    end
    print "\n"
    puts "Appuyez pour quitter !"
    puts "########################################################################"
    gets.chomp
end

def perform(journalists)
    premier(journalists)
    deuxieme(journalists)
    troisieme(journalists)
    quatrieme(journalists)
    cinquieme(journalists)
    sixieme(journalists)
    septieme(journalists)
    huitieme(journalists)
end

perform(journalists)